import axios from 'axios';
export default {
  data() {
    return {
      isAdmin: false,
      isManager: false,
      isLoggedIn: false
    };
  },
  async created() {
    await this.checkUserRole();
  },
  methods: {
    async checkUserRole() {
      try {
        const response = await axios.get('http://localhost:8080/WebShopAppREST/rest/currentUser');
        const currentUser = response.data;
        console.log('Trenutni korisnik:', currentUser);
        this.isLoggedIn = !!currentUser;
        if (this.isLoggedIn) {
          this.isAdmin = currentUser.role === 'ADMIN';
          this.isManager = currentUser.role === 'MANAGER';
        }
      } catch (error) {
        console.error('Greška pri proveri uloge korisnika:', error);
        this.isLoggedIn = false;
        this.isAdmin = false;
        this.isManager = false;
      }
    }
  }
};
