export default {
  computed: {
    // Pretpostavljam da koristiš localStorage za čuvanje trenutnog korisnika
    currentUser() {
      const user = localStorage.getItem('currentUser');
      console.log('Current User:', user); // Proveravamo šta se čuva u localStorage
      return user ? JSON.parse(user) : null;
    },
    isLoggedIn() {
      return !!this.currentUser; // Proveravamo da li postoji korisnik u localStorage
    },
    isAdmin() {
      return this.currentUser && this.currentUser.role === 'ADMIN'; // Proveravamo da li je korisnik admin
    },
    isManager() {
      return this.currentUser && this.currentUser.role === 'MANAGER'; // Proveravamo da li je korisnik menadžer
    },
    isWorker() {
      return this.currentUser && this.currentUser.role === 'WORKER'; // Proveravamo da li je korisnik radnik
    }
  }
};
