import axios from 'axios';
export default {
  data() {
    return {
      isAdmin: false,
      isLoggedIn: false
    };
  },
  async created() {
    await this.checkUserRole();
  },
  watch: {
    // Kada se ruta promeni, proveravamo status korisnika
    '$route'(to, from) {
      this.checkUserRole();
    }
  },
  methods: {
    async checkUserRole() {
      try {
        const response = await axios.get('http://localhost:8080/WebShopAppREST/rest/currentUser');
        const currentUser = response.data;
        this.isLoggedIn = !!currentUser;
        this.isAdmin = currentUser && currentUser.role === 'ADMIN';
      } catch (error) {
        console.error('Error checking user role:', error);
        this.isLoggedIn = false;
        this.isAdmin = false;
      }
    }
  }
};
