export default {
  data() {
    return {
      isLoggedIn: false // Inicijalno postavljeno na false, jer korisnik nije prijavljen
    };
  },
  created() {
    // Provera da li je korisnik prijavljen prilikom kreiranja komponente
    this.checkLoginStatus();
  },
  methods: {
    checkLoginStatus() {
      // Implementiraj logiku za proveru da li je korisnik prijavljen
      // Na primer, provera da li postoji token u lokalnom skladištu ili nešto slično
      // Ako je korisnik prijavljen, postavi isLoggedIn na true, inače na false
      const token = localStorage.getItem('token'); // Primer provere tokena u lokalnom skladištu
      this.isLoggedIn = !!token;
    }
  }
};
