import "core-js/modules/es.array.push.js";
import { ref, onMounted, computed } from 'vue';
import axios from 'axios';
import router from '@/router'; // Importujte instancu rutera

export default {
  __name: 'AllChocolateFactories',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const factories = ref([]);
    async function loadFactories() {
      try {
        const response = await axios.get('http://localhost:8080/WebShopAppREST/rest/chocolateFactoryObjects');
        factories.value = response.data;
      } catch (error) {
        console.error("Greška pri učitavanju fabrika:", error);
      }
    }
    onMounted(() => {
      loadFactories();
    });
    const isFactoryOpen = factory => {
      const currentTime = new Date();
      const openingTime = new Date();
      const closingTime = new Date();
      const [openHour, openMinute] = factory.openingTime.split(':');
      const [closeHour, closeMinute] = factory.closingTime.split(':');
      openingTime.setHours(openHour, openMinute);
      closingTime.setHours(closeHour, closeMinute);
      return currentTime >= openingTime && currentTime <= closingTime;
    };
    const sortedFactories = computed(() => {
      return factories.value.sort((a, b) => {
        const aOpen = isFactoryOpen(a);
        const bOpen = isFactoryOpen(b);
        if (aOpen && !bOpen) return -1;
        if (!aOpen && bOpen) return 1;
        return 0;
      });
    });
    const getFactoryLogo = logoFileName => {
      return `/images/${logoFileName}`;
    };
    const viewFactoryProfile = factory => {
      console.log("Prikaz profila fabrike:", factory);
      router.push({
        name: 'factoryprofile',
        params: {
          id: factory.id
        }
      });
    };
    const __returned__ = {
      factories,
      loadFactories,
      isFactoryOpen,
      sortedFactories,
      getFactoryLogo,
      viewFactoryProfile,
      ref,
      onMounted,
      computed,
      get axios() {
        return axios;
      },
      get router() {
        return router;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
