import axios from 'axios';
export default {
  data() {
    return {
      isAdmin: false,
      isManager: false,
      isLoggedIn: false
    };
  },
  async created() {
    await this.checkUserRole();
  },
  watch: {
    // Prati promene u ruti i ponovo proverava ulogu korisnika
    $route(to, from) {
      this.checkUserRole();
    }
  },
  methods: {
    async checkUserRole() {
      try {
        const response = await axios.get('http://localhost:8080/WebShopAppREST/rest/currentUser');
        const currentUser = response.data;
        if (currentUser && currentUser.role) {
          this.isLoggedIn = true;
          this.isAdmin = currentUser.role === 'ADMIN';
          this.isManager = currentUser.role === 'MANAGER';
        } else {
          this.isLoggedIn = false;
          this.isAdmin = false;
          this.isManager = false;
        }
      } catch (error) {
        console.error('Greška pri proveri uloge korisnika:', error);
        this.isLoggedIn = false;
        this.isAdmin = false;
        this.isManager = false;
      }
    }
  }
};
