export default {
  data() {
    return {};
  },
  computed: {
    // Pretpostavljam da koristiš Vuex ili lokalnu promenljivu za praćenje stanja korisnika
    currentUser() {
      // Ovo možeš zameniti logikom kojom dohvaćaš ulogovanog korisnika (npr. iz Vuex store-a)
      return JSON.parse(localStorage.getItem('currentUser')) || null;
    },
    isLoggedIn() {
      return !!this.currentUser;
    },
    isAdmin() {
      return this.currentUser && this.currentUser.role === 'ADMIN';
    },
    isManager() {
      return this.currentUser && this.currentUser.role === 'MANAGER';
    },
    isWorker() {
      return this.currentUser && this.currentUser.role === 'WORKER';
    }
  }
};
